import * as outputsService from "../../outputsService.js";
import { buildChartData } from "../../chartSeriesBuilders.js";
export default {
  name: 'TwoCharts',
  components: {
    'StackedAreaChart': function StackedAreaChart() {
      return import("../../charts/StackedAreaChart.vue");
    },
    'LineChart': function LineChart() {
      return import("../../charts/LineChart.vue");
    },
    'StackedAreaChart100Percent': function StackedAreaChart100Percent() {
      return import("../../charts/StackedAreaChart100Percent.vue");
    }
  },
  props: ['subtab', 'outputs', 'chartDefinitions', 'countrycode', 'appState'],
  data: function data() {
    return {
      chart1Data: {},
      chart2Data: {},
      chart1Component: undefined,
      chart2Component: undefined,
      infoComponent: undefined
    };
  },
  computed: {
    chart1HasNegativeData: function chart1HasNegativeData() {
      var currChart = this.chartDefinitions[this.subtab.charts[0]];
      return currChart.type === "StackedAreaNegative";
    },
    chart2HasNegativeData: function chart2HasNegativeData() {
      var currChart = this.chartDefinitions[this.subtab.charts[0]];
      return currChart.type === "StackedAreaNegative";
    }
  },
  methods: {
    buildDataForChart1: function buildDataForChart1() {
      var currChart = this.chartDefinitions[this.subtab.charts[0]];
      var chartData = buildChartData(this.countrycode, currChart, this.outputs, this.chart1HasNegativeData);

      if (chartData) {
        this.chart1Data = chartData;
      } //possible to add color info, css classes, sort the series etc

    },
    buildDataForChart2: function buildDataForChart2() {
      var currChart = this.chartDefinitions[this.subtab.charts[1]];
      var chartData = buildChartData(this.countrycode, currChart, this.outputs, this.chart2HasNegativeData);

      if (chartData) {
        this.chart2Data = chartData;
      } //possible to add color info, css classes, sort the series etc

    },
    loadChart1Component: function loadChart1Component() {
      var currChart = this.chartDefinitions[this.subtab.charts[0]]; // console.log('loadChartComponent ', currChart, this.subtab, this.chartDefinitions);

      if (currChart) {
        this.chart1Component = outputsService.chartTypeToComponentMap[currChart.type];
      }
    },
    loadChart2Component: function loadChart2Component() {
      var currChart = this.chartDefinitions[this.subtab.charts[1]]; // console.log('loadChartComponent ', currChart, this.subtab, this.chartDefinitions);

      if (currChart) {
        this.chart2Component = outputsService.chartTypeToComponentMap[currChart.type];
      }
    },
    loadInfoComponent: function loadInfoComponent() {
      var _this = this;

      var fileName = this.subtab.charts[0] + '.vue'; //import() only works with partial paths, it doesn't take a single variable, like import(filePath). import() is not a function!! It just looks like one
      //This rest is a bit hacky, but well.
      //The first import() looks if the file exists, if not the error is silently caught.
      //If it exists, a second import needs to be started because <component> cannot work directly with the answer of the
      //first succesful fetch. It can only work with a function like this (for dynamic imports):
      //infoComponent = () => import(`../content/${fileName}`)

      import("./content/".concat(fileName)).then(function () {
        _this.infoComponent = function () {
          return import("./content/".concat(fileName));
        };
      }).catch(function () {// console.log('info component not found');
      });
    }
  },
  created: function created() {
    this.loadChart1Component();
    this.loadChart2Component();

    if (this.appState.lastAPIResponse) {
      // only call buildDataForChart when data is already available!
      this.buildDataForChart1();
      this.buildDataForChart2();
    }

    this.loadInfoComponent();
  },
  watch: {
    'appState.lastAPIResponse': function appStateLastAPIResponse() {
      this.buildDataForChart1();
      this.buildDataForChart2();
    },
    countrycode: function countrycode() {
      this.buildDataForChart1();
      this.buildDataForChart2();
    }
  }
};
